mapper.select:
  	select rnum,userId,productName,regdate,productNo,imgSname
      from (
          select rownum as rnum,userId,productName,regdate,productNo,imgSname
          from (
              select w.user_id as userId, p.product_name as productName,w.regdate as regdate,w.product_no as productNo,i.img_sname as imgSname
              from wishlist w
			  join products p
			  on w.product_no = p.product_no
			  join products_img i
			  on p.product_no = i.product_no
			  where i.img_state=1
			  and w.user_id = #{userId}
			  order by regdate desc
          )
          where rownum <= #{pager.endRowNo}
      )
      where rnum >= #{pager.startRowNo}

mapper.delete:
delete from wishlist 
where product_no=#{productNo}
and user_id = #{userId}
